package Lezione1;

public class SommaProdotto {
	
	public static int Somma (int a,int b) {
		int somma = a+b;
		return somma;
	}
	public static  int Prodotto(int a, int b) {
		int prodotto = a*b;
		return prodotto;
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Somma(1,1));
		System.out.println(Prodotto(1,1));
		
	}

}
